package com.future.config;import com.future.lock.ZkLock;import java.util.concurrent.TimeUnit;import java.util.concurrent.locks.Lock;/** * @Description: * @Author: lilei58 * @Date: Created in 2022/1/5 上午7:16 */public class Main {    private static Lock lock = new ZkLock();    public static void main(String[] args) {        ZkConfig config = new ZkConfig();        config.save("timeout", "100");        for (int i = 0; i < 100; i++) {            System.out.println(config.get("timeout"));            try {                TimeUnit.SECONDS.sleep(3);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }}